import React, { useEffect } from 'react';
import { io } from 'socket.io-client';

const socket = io('http://localhost:5000');  // Connecte-toi à ton serveur backend

const App = () => {
  useEffect(() => {
    // Ecouter les événements venant du serveur
    socket.on('connect', () => {
      console.log('Connected to server with id:', socket.id);
    });

    // Ecoute un événement spécifique du serveur (par exemple, la création d'un lobby)
    socket.on('lobbyCreated', (data) => {
      console.log('Lobby created with code:', data.lobbyCode);
      // Traite les données et mets à jour l'état si nécessaire
    });

    // Nettoyer la connexion lorsque le composant se démonte
    return () => {
      socket.off('connect');
      socket.off('lobbyCreated');
    };
  }, []);

  return (
    <div>
      <h1>Mon jeu</h1>
      <button onClick={() => socket.emit('createLobby', { player: 'Player1' })}>
        Créer un salon
      </button>
    </div>
  );
};

export default App;
