import React, { useState } from 'react';

const Home: React.FC = () => {
    const [name, setName] = useState('');

    const handleJoinLobby = () => {
        // Logic for joining a lobby
        console.log(`Joining lobby as ${name}`);
    };

    const handleCreateLobby = () => {
        // Logic for creating a lobby
        console.log(`Creating lobby as ${name}`);
    };

    return (
        <div style={{ textAlign: 'center', marginTop: '50px' }}>
            <h1>Welcome to Onskone</h1>
            <input
                type="text"
                placeholder="Enter your name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                style={{ padding: '10px', fontSize: '16px', marginBottom: '20px' }}
            />
            <div>
                <button onClick={handleJoinLobby} style={{ padding: '10px 20px', fontSize: '16px', marginRight: '10px' }}>
                    Join Lobby
                </button>
                <button onClick={handleCreateLobby} style={{ padding: '10px 20px', fontSize: '16px' }}>
                    Create Lobby
                </button>
            </div>
        </div>
    );
};

export default Home;
